import React from 'react';
import stockTag from "../src/components/atoms/stockTag";

var cardData = [
    {
        iconType:'bank',
        iconStyle: {fontSize: "30px", marginTop: '30%', color: '#F88998'},
        title: '25',
        description: 'Items Out of Stock'
    },
    {
        iconType:'shopping-cart',
        iconStyle: {fontSize: "30px", marginTop: '30%', color: '#FFB353'},
        title: '25',
        description: 'Items Out of Stock'
    },
    {
        iconType:'rise',
        iconStyle: {fontSize: "30px", marginTop: '30%', color: '#69E4A6'},
        title: '25',
        description: 'Items Out of Stock'
    }
]
var cascaderData =[
    {
        placeholder: 'All Categories',
        optionArray: ['Biscuits', 'Chips', 'Drinks', 'Fries', 'Breads']
        // style: { width: 162 }
    },
    {
        placeholder: 'All Inventory',
        optionArray: ['Biscuits', 'Chips', 'Drinks', 'Fries', 'Breads']
        // style: { width: 162 }
    },
    {
        placeholder: 'All Stores',
        optionArray: ['Biscuits', 'Chips', 'Drinks', 'Fries', 'Breads']
        // style: { width: 162 }
    },
    {
        placeholder: 'All Actions',
        optionArray: ['Biscuits', 'Chips', 'Drinks', 'Fries', 'Breads']
        // style: { width: 162 }
    }
]

var productColumns =[
    {
        title: 'PRODUCT',
        dataIndex: 'product',
        key: 'product'
    },
    {
        title: 'CODE',
        dataIndex: 'code',
        key: 'code'
    },
    {
        title: 'CATEGORY',
        dataIndex: 'category',
        key: 'category'
    },
    {
        title: 'LOCATION',
        dataIndex: 'location',
        key: 'location'
    },
    {
        title: 'INVENTORY',
        dataIndex: 'inventory',
        key: 'inventory',
        render: inventory => (<span>{stockTag(inventory)}</span>)
    },
    {
        title: 'PRICE',
        key: 'price',
        dataIndex: 'price',
        render: price => <span><b>INR {price}</b></span>
    }
]

 const productColumnData =[
     {
         product: 'Parle G',
         code: '1X2X3X4X5',
         category: 'Biscuits',
         location: 'BBSR',
         inventory: [20],
         price: 10
     },
     {
        product: 'Hide & Seek',
        code: '1X2X3X4X5',
        category: 'Biscuits',
        location: 'BBSR',
        inventory: [200],
        price: 30
    },
    {
        product: 'Parle G',
        code: '1X2X3X4X5',
        category: 'Biscuits',
        location: 'BBSR',
        inventory: [2],
        price: 10
    },
    {
        product: 'Parle G',
        code: '1X2X3X4X5',
        category: 'Biscuits',
        location: 'BBSR',
        inventory: [20],
        price: 10
    },
    {
       product: 'Hide & Seek',
       code: '1X2X3X4X5',
       category: 'Biscuits',
       location: 'BBSR',
       inventory: [200],
       price: 30
   },
   {
       product: 'Parle G',
       code: '1X2X3X4X5',
       category: 'Biscuits',
       location: 'BBSR',
       inventory: [2],
       price: 10
   }
 ]

export const itemData = {
    cardData, cascaderData, productColumns, productColumnData
}
